mapper.delete:
		DELETE FROM files
		WHERE file_id IN

        		#{fileId}

		AND related_id = #{projectId}

mapper.insert:

insert into files (
   file_id, related_id, file_name, file_type, file_data, file_regdate
) values (
  files_seq.nextval,  #{taskId}, #{fileName}, #{fileType}, #{fileData}, to_char(sysdate, 'yyyymmddhh24miss')
)


insert into files (
   file_id, related_id, file_name, file_type, file_data, file_regdate
) values (
  files_seq.nextval,  #{messageId}, #{fileName}, #{fileType}, #{fileData}, to_char(sysdate, 'yyyymmddhh24miss')
)

mapper.select:
select file_id, related_id, file_name, file_type, file_data
from files
where file_id = #{fileId}
