mapper.insert:

        select notice_id
		from (
		    select notice_id from notice
		    order by notice_regdate desc)
		where rownum = 1

	insert into notice (
		member_id, project_id, notice_title, notice_content,
		notice_regdate, notice_hitnum, notice_enabled
	) values (
		#{memberId}, #{projectId}, #{noticeTitle}, #{noticeContent}, 
		to_char(sysdate, 'yyyymmddhh24miss'), 1, #{noticeEnabled}
	)


insert into files (
    related_id, file_name, file_type, file_data, file_regdate
) values (
    #{noticeId}, #{fileName}, #{fileType}, #{fileData}, to_char(sysdate, 'yyyymmddhh24miss')
)

mapper.select:

select notice_num, rownum as rnum, notice_id, project_id, member_id, notice_title, notice_content,
			notice_regdate, notice_hitnum, notice_enabled
from(
	select notice_num, rownum as rnum, notice_id, project_id, member_id, notice_title, notice_content,
			notice_regdate, notice_hitnum, notice_enabled
	from (
		select 
			row_number() over (order by notice_regdate) as notice_num, notice_id, project_id, member_id, notice_title, notice_content,
			notice_regdate, notice_hitnum, notice_enabled
		from
			notice
		where notice_enabled = 1
		order by notice_regdate	desc
	) 
	where rownum <= #{endRowNo}		
)			
where rnum >= #{startRowNo}

mapper.update:
update notice
set 
	notice_enabled = #{noticeEnabled}, 
	notice_update_date = to_char(sysdate, 'yyyymmddhh24miss'),
	notice_update_mid = #{memberId}
where notice_id = #{noticeId}

update notice
set notice_hitnum = notice_hitnum + 1 
where notice_id = #{noticeId}

mapper.select:
select file_id, related_id, file_name, file_type, file_data
from files
where file_id = #{fileId}
